from django.db import models

class User(models.Model):
    name = models.TextField()

    def __str__(self):
        return  {} - {} .format(str(self.id), self.name)


class Task(models.Model):
    name = models.TextField()
    assign = models.ForeignKey(User, on_delete=models.CASCADE)

